import React, { useState } from 'react';

const LoginForm = () => {
 const [formValidity, setFormValidity] = useState({});
 //... other code

const handleValidation = (input) => {
  const name = input.name;
  const value = input.value;
 
  if (name === "email") {
     if (/.+@.+\..+/.test(value)) {
       setFormValidity({ ...formValidity, email: true });
     } else {
       setFormValidity({ ...formValidity, email: false });
     }
  } else if (name === "password") {
     if (value.length >= 6) {
       setFormValidity({ ...formValidity, password: true });
     } else {
       setFormValidity({ ...formValidity, password: false });
     }
  }
}
 const handleChange = (event) => {
  handleValidation(event.target)
 }

 return (
  <form>
     <label>Email:</label>
     <input
       type="text"
       name="email"
       onChange={handleChange}
       onBlur={(event) => handleValidation(event.target)}
     />
     <br />
     <label>Password:</label>
     <input
       type="password"
       name="password"
       onChange={handleChange}
       onBlur={(event) => handleValidation(event.target)}
     />
     <br />
     <button type="submit" disabled={!formValidity.email || !formValidity.password}>
       Submit
     </button>
  </form>
 )};
 export default LoginForm
