// TimeSlotModal.js
import React, { useState } from 'react';
import './Modal.css'; // Import the modal styles

const TimeSlotModal = ({ isOpen, onClose, onAddTimeSlot }) => {
  const [timeSlot, setTimeSlot] = useState('');

  const handleAddTimeSlot = (childre) => {
    // Validate the time slot if needed
    if (timeSlot.trim() !== '') {
      onAddTimeSlot(timeSlot);
      setTimeSlot(''); // Clear the input field
    }
  };

  return (
    <div className={`modal ${isOpen ? 'show' : 'hide'}`}>
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>Add Time Slot</h2>
        <label htmlFor="timeSlot">Time Slot:</label>
        <input
          type="text"
          id="timeSlot"
          value={timeSlot}
          onChange={(e) => setTimeSlot(e.target.value)}
        />
        <button onClick={handleAddTimeSlot}>Add Time Slot</button>
      </div>
    </div>
  );
};

export default TimeSlotModal;
