  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBCheckbox,
  MDBIcon
}
from 'mdb-react-ui-kit';


function SignUpBox({setBoxName}){

  const handleLogin=()=>{
    setBoxName('login')
}
const [signUpData,setSignUpData]=useState(
  { 
    fName:'',
    lName:'',
    email:'',
    password:'',
    confirmPassword:'',
    })
    
    useEffect(()=>{
      console.log(signUpData);
