  }
};


 
 return (
  
 
  <MDBCol md='6' >

 <MDBCard className='my-5 bg-glass'>
 <MDBCardBody className='p-5'>
 <MDBRow>
  <MDBCol col='4'>
    <MDBInput
      wrapperClass='mb-4'
      label='First name'
      id='form1'
      type='fName'
      value={signUpData.fName}
      onChange={(e) => { setSignUpData({ ...signUpData, fName: e.target.value }); }}
    />
  </MDBCol>

  <MDBCol col='4'>
    <MDBInput
      wrapperClass='mb-4'
      label='Last name'
      id='form2'
      type='lName'
      validate
      error='wrong'
      success='right'
      required
      value={setSignUpData.lName}
      onChange={(e) => { setSignUpData({ ...signUpData, lName: e.target.value }); }}
    />
  </MDBCol>
</MDBRow>

<MDBInput
  wrapperClass='mb-4'
  label='Email'
  icon='envelope' group
  id='form3'
  type='email'
  validate
  error='wrong'
 success='right'
  value={signUpData.email}
  onChange={(e) => { setSignUpData({ ...signUpData, email: e.target.value }); }}
/>
<MDBInput

label='Your password'
icon='lock'
group
type='password'
validate
required
value={signUpDatapassword}
  onChange={(e) => { setSignUpData({ ...signUpData, password: e.target.value }); }}
/>
<MDBInput
  wrapperClass='mb-4'
  label='Confirm Password'
  id='form5'
  type='password'
  onChange={(e) => { setSignUpData({ ...signUpData, confirmPassword: e.target.value }); }}
/>

   

   <MDBBtn className='w-100 mb-4' size='md'onClick={handleRegister}>sign up</MDBBtn>

   <div className="text-center">
  
<p><span onClick={handleLogin}>go to login</span> </p>
    
     <p>or sign up with:</p>
    
   </div> 

 </MDBCardBody>
</MDBCard>

</MDBCol>
 )
}
export default SignUpBox;
