import React, { useState } from 'react';
import './AddCourtForm.css'; // Import your CSS file
import MainNavBar from './MainNavBar'; // Import your main navbar component

const AddCourtForm = () => {
  const [formData, setFormData] = useState({
    courtName: '',
    sportType: '',
    location: '',
    description: '',
    address: '',
    courtImage: null, // Add courtImage to the form data
  });

  const [selectedImage, setSelectedImage] = useState('');

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    // If the input is a file input, use files[0] to get the selected file
    const inputValue = type === 'file' ? files[0] : value;

    setFormData({
      ...formData,
      [name]: inputValue,
    });

    if (type === 'file') {
      // Set the selectedImage state with the URL of the selected image
      const imageUrl = URL.createObjectURL(inputValue);
      setSelectedImage(imageUrl);
    }
  };

  const validateForm = () => {
    let isValid = true;
    const newErrors = {};

    // ... (previous validation logic)

    if (!formData.courtImage) {
      newErrors.courtImage = 'Court image is required'; // Add courtImage validation
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Perform your form submission logic here
      console.log('Form submitted:', formData);
    }
  };

  return (
    <div>
      <MainNavBar />
      <div className="add-court-form">
        <h2>Add a Court</h2>
        <form onSubmit={handleSubmit}>
          {/* ... (previous form fields) */}

          <div className="form-group">
            <label htmlFor="courtImage">Court Image</label>
            <input
              type="file"
              id="courtImage"
              name="courtImage"
              accept="image/*"
              onChange={handleChange}
            />
            {errors.courtImage && <p className="error-message">{errors.courtImage}</p>}
          </div>
          {selectedImage && <img src={selectedImage} alt="" width={'50px'} />}
          <button type="submit">Add Court</button>
        </form>
      </div>
    </div>
  );
};

export default AddCourtForm;
