import React, { useState } from 'react';
import axios from 'axios'; // Make sure to import axios
import './LoginBox.css';
import { BASE_URL } from '../../Constants/constants';

const LoginBox = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    let errors = {};
    if (email.trim() === '') {
      errors.email = 'Email is required';
    }
    if (password.trim() === '') {
      errors.password = 'Password is required';
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    if (!validateForm()) {
      // If the form is not valid, don't proceed with the API call
      return;
    }
      // Perform your API call here to send the data to the backend
      try {
        const response = await axios.post(`${BASE_URL}/auth/login`, { email, password }).then((res)=>{
if(res.data.message==="login successful"&& res.data.token){

