// TimeSlotModal.js
import React from 'react';
import Modal from 'react-modal';

export function ModalView({ children, setModalOpen, modalOpen }) {
  const customStyles = {
    overlay: {
      display: 'none',
      position: 'fixed',
      zIndex: 1,
      left: 0,
      top: 0,
      width: '100%',
      height: '100%',
      overflow: 'auto',
      backgroundColor: 'rgba(0, 0, 0, 0.4)',
      paddingTop: '60px',
    },
    modalContent: {
      backgroundColor: '#fefefe',
      margin: '5% auto',
      padding: '20px',
      border: '1px solid #888',
      width: '80%',
    },
    close: {
      color: '#aaa',
      float: 'right',
      fontSize: '28px',
      fontWeight: 'bold',
    },
    show: {
      display: 'block',
    },
    hide: {
      display: 'none',
    },
  };

  function afterOpenModal() {}

  function openModamodalOpel() {
    setModalOpen(true);
  }

  function closeModal() {
    setModalOpen(false);
  }

  return (
    <div>
      <Modal
        isOpen={modalOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Modal Content"
      >
        {children}
      </Modal>
    </div>
  );
}

export default TimeSlotModal;
