// TimeSlotModal.js
import React from 'react';
import Modal from 'react-modal';

const TimeSlotModal = ({ children, isOpen, onRequestClose, contentLabel }) => {
  const customStyles = {
    overlay: {
      position: 'fixed',
      zIndex: 1,
      left: 0,
      top: 0,
      width: '100%',
      height: '100%',
      overflow: 'auto',
      backgroundColor: 'rgba(0, 0, 0, 0.4)',
      paddingTop: '60px',
    },
    modalContent: {
      backgroundColor: '#fefefe',
      margin: '5% auto',
      padding: '20px',
      border: '1px solid #888',
      width: '80%',
    },
    close: {
      color: '#aaa',
      float: 'right',
      fontSize: '28px',
      fontWeight: 'bold',
      cursor: 'pointer',
    },
  };

  Modal.setAppElement('#root');

  const afterOpenModal = () => {
    // You can perform actions after the modal has opened
  };

  const openModal = () => {
    onRequestClose(true);
  };

  const closeModal = () => {
    onRequestClose(false);
  };

  return (
    <Modal
      isOpen={isOpen}
      onAfterOpen={afterOpenModal}
      onRequestClose={closeModal}
      style={customStyles}
      contentLabel={contentLabel}
    >
      <div style={customStyles.modalContent}>
        <span style={customStyles.close} onClick={closeModal}>
          &times;
        </span>
        {children}
      </div>
    </Modal>
  );
};

export default TimeSlotModal;
