import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import './nav.css';
import { setUserDetails } from '../../toolkit/userSlice';

export default function NavBar() {
  const dispatch = useDispatch();
  const { userDetails } = useSelector(state => state.user);

  const handleLogout = () => {
    // Add your logout logic here
    console.log('Logout button clicked');
  };

  // Example: Dispatching an action when the component mounts
  useEffect(() => {
    // Assuming you have an initial user object or fetch it from somewhere
    const initialUser = {
      fName: 'John',
      // other user details...
    };

    // Dispatching setUserDetails with the initial user details
    dispatch(setUserDetails(initialUser));
  }, [dispatch]);

  return (
    <div>
      <header>
        <nav>
          <div className="logo">Your Logo</div>
          <ul className="nav-links">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/booking">Book a Turf</Link></li>
            <li><Link to="/about">About Us</Link></li>
            <li><Link to="/contact">Contact</Link></li>
          </ul>
          <div className="user-details">
            <p>Welcome, {userDetails.fName}</p>
          </div>
          <button className="logout-button" onClick={handleLogout}>Logout</button>
        </nav>
      </header>

      <section className="hero">
        <h1>Welcome to Your Turf Booking Site</h1>
        <p>Book the best turfs for your sports activities!</p>
        <Link to="/booking" className="cta-button">Book Now</Link>
      </section>

      {/* Add more sections and content as needed */}

      <footer>
        <p>&copy; 2023 Your Turf Booking Site. All rights reserved.</p>
      </footer>
    </div>
  );
}
