// CourtBookingPage.js

import React, { useState } from 'react';
import MainNavBar from '../Components/Common/MainNavBar';
import AxiosInstance from '../config/AxiosInstance';
import { useParams } from 'react-router-dom';

const CourtBooking = () => {
    const{id}=useParams()
  // Example court data (replace this with actual data)
  const courtData = {
    courtName: 'Sample Court',
    courtImage: 'book\assets\turf2.jpeg', // Replace with the actual image path
  };

  const [timeslot, setTimeslot] = useState('');

  const handleAddTimeslot = async () => {
    try {
      // Perform the API request to add the timeslot
      const response = await AxiosInstance.post('/api/addTimeslot', {
        courtId: 'sampleCourtId', // Replace with the actual court ID
        timeslot: timeslot,
      });

      // Handle success (you can show a success message or perform additional actions)
      console.log('Timeslot added successfully:', response.data);
    } catch (error) {
      // Handle error (show an error message or perform error-related actions)
      console.error('Error adding timeslot:', error);
    }
  };

  return (
    <>
      <MainNavBar />
      <div className="container">
        <div className="row mt-4">
          <div className="col-md-6">
            <h2>{courtData.courtName}</h2>
            <img
              src={courtData.courtImage}
              alt={courtData.courtName}
              className="img-fluid"
            />
          </div>
          <div className="col-md-6">
            <h3>Add Timeslot</h3>
            <div className="mb-3">
              <label htmlFor="timeslot" className="form-label">
                Timeslot:
              </label>
              <input
                type="text"
                id="timeslot"
                className="form-control"
                value={timeslot}
                onChange={(e) => setTimeslot(e.target.value)}
              />
            </div>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleAddTimeslot}
            >
              Add Timeslot
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default CourtBookingPage;
