import React, { useEffect, useState } from 'react';
import './SignUpBox.css';
import axios from 'axios';
import {BASE_URL}from '../../Constants/constants';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBCheckbox,
  MDBIcon
}
from 'mdb-react-ui-kit';


function SignUpBox({setBoxName}){

  const handleLogin=()=>{
    setBoxName('login')
}
const [signUpData,setSignUpData]=useState(
  { 
    fName:'',
    lName:'',
    email:'',
    password:'',
    confirmPassword:'',
    })
    
    useEffect(()=>{
      console.log(signUpData);
    },[signUpData])
    
      
    
  
 /*const [fname, setFname] = useState('');
  const [lname, setLname] = useState('');

 const [email, setEmail] = useState('');
 const [password, setPassword] = useState('');
 const [confirmPassword, setConfirmPassword] = useState('');

 const [error, setError] = useState('');*/
 const handleRegister = async () => {
  try {
    const res= await axios.post(`${BASE_URL}/auth/signUp`, signUpData).then((res)=>{
    debugger;
    console.log(res)});
     /*if(signUpData.data.message === 'User created') 
     {
      setBoxName('login');
    }

    if(a.message === "email already exist") {
      alert('Email already exists');
      debugger;
    }*/
  } catch (error) {
    
  }
};


 
 return (
  
 <MDBContainer col >
  <MDBRow>
  <MDBCol md='6'className='offset-md-3 d-flex' >

  <form onSubmit={handleRegister}>
 <p className='='h4 text-center>SigN Up FoRm</p>
 <div className='grey-text'>
 <MDBInput
      
      label='First name'
      id='form1'
      type='fName'
      value={signUpData.fName}
      onChange={(e) => { setSignUpData({ ...signUpData, fName: e.target.value }); }}
    />

  
    <MDBInput  className='required-field'
      
      label='Last name'
      id='form2'
      type='lName'
      validate
      error='wrong'
      success='right'
      required
      value={setSignUpData.lName}
      onChange={(e) => { setSignUpData({ ...signUpData, lName: e.target.value }); }}
    />
  
<MDBInput  className='required-field'
  
  label='Email'
  icon='envelope' group
  id='form3'
  type='email'
  validate
  error='wrong'
 success='right'
  value={signUpData.email}
  onChange={(e) => { setSignUpData({ ...signUpData, email: e.target.value }); }}
/>
<MDBInput className='required-field'

label='Your password'
icon='lock'
group
type='password'
validate
required
value={signUpData.password}
  onChange={(e) => { setSignUpData({ ...signUpData, password: e.target.value }); }}
/>
<MDBInput className='required-field'
  wrapperClass='mb-4'
  label='Confirm Password'
  id='form5'
  type='password'
  value={signUpData.confirmPassword}
  onChange={(e) => { setSignUpData({ ...signUpData, confirmPassword: e.target.value }); }}
/>
<p className='text-danger' id='confirmPasswordError'></p>
 </div>
 <div className='text-center'>
              <MDBBtn color='primary' type='submit'>
                Register
              </MDBBtn>
            </div>
          </form>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
 );
};
export default SignUpBox;
