import React, { useEffect, useState } from 'react';
import './SignUpBox.css';
import axios from 'axios';
import {BASE_URL}from '../../Constants/constants';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBCheckbox,
  MDBIcon
}
from 'mdb-react-ui-kit';
function SignUpBox({setBoxName}){

  const handleLogin=()=>{
    setBoxName('login')
}
const [signUpData,setsignUpDAta]=useState(
  {
    fName:'',
    lName:'',
    email:'',
    password:'',
    confirmPassword:'',
    })
    
    useEffect(()=>{
      console.log(signUpData);
    },[signUpData])
    
      
    
  
 /*const [fname, setFname] = useState('');
  const [lname, setLname] = useState('');

 const [email, setEmail] = useState('');
 const [password, setPassword] = useState('');
 const [confirmPassword, setConfirmPassword] = useState('');

 const [error, setError] = useState('');*/
 const handleRegister = async () => {
  try {
    const response=await axios.post(`${BASE_URL}/auth/signUp`, signUpData);
    debugger;
    console.log(response)
    if (response.data.message === "signup success") {
      setBoxName('login');
    }

    if (response.data.message === "email already exist") {
      alert('Email already exists');
      debugger;
    }
  } catch (error) {
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      console.error("Server responded with non-success status", error.response.status);
    } else if (error.request) {
      // The request was made but no response was received
      console.error("No response received from server");
    } else {
      // Something happened in setting up the request that triggered an Error
      console.error("Error during request setup", error.message);
    }
   // alert('Something went wrong');
  }
};


 
 return (
  
 
  <MDBCol md='6' >

 <MDBCard className='my-5 bg-glass'>
 <MDBCardBody className='p-5'>

   <MDBRow>
     <MDBCol col='4'>
       <MDBInput wrapperClass='mb-4' label='First name' id='form1' type='text' value={signUpData.fName} onChange={(e)=>{setsignUpDAta({...setsignUpDAta,fName:e.target.value })}}/>
       
     </MDBCol>

     <MDBCol col='4'>
       <MDBInput wrapperClass='mb-4' label='Last name' id='form2' type='text'value={signUpData.lName} onChange={(e)=>{setsignUpDAta({...setsignUpDAta,lName:e.target.value })}}/>
     </MDBCol>
   </MDBRow>

   <MDBInput wrapperClass='mb-4' label='Email' id='form3' type='email'value={signUpData.email} onChange={(e)=>{setsignUpDAta({...setsignUpDAta,email:e.target.value })}}/>
   <MDBInput wrapperClass='mb-4' label='Password' id='form4' type='password'onChange={(e)=>{setsignUpDAta({...setsignUpDAta,password:e.target.value })}}/>
   <MDBInput wrapperClass='mb-4' label='confirm Password' id='form5' type='password'onChange={(e)=>{setsignUpDAta({...setsignUpDAta,confirmPassword:e.target.value })}}/>

   

   <MDBBtn className='w-100 mb-4' size='md'onClick={handleRegister}>sign up</MDBBtn>

   <div className="text-center">
  
<p><span onClick={handleLogin}>go to login</span> </p>
    
     <p>or sign up with:</p>
    
   </div> 

 </MDBCardBody>
</MDBCard>

</MDBCol>
 )
}
export default SignUpBox;
