import React, { useState } from 'react';
import axios from 'axios';
import './LoginBox.css';
import { BASE_URL } from '../../Constants/constants'
 import { useNavigate } from 'react-router-dom';
 const navigate = useNavigate();
 

const LoginBox = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateForm = () => {
    let errors = {};
    if (email.trim() === '') {
      errors.email = 'Email is required';
    }
    if (password.trim() === '') {
      errors.password = 'Password is required';
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleLogin =async  (e) => {
    
      e.preventDefault(); // <-- prevent default form action
    
    if (!validateForm()) {
      return;
    }

    try {
    debugger;
      if (email && password) {
        
      await axios.post(`${BASE_URL}/auth/login`,{ email,password }).then((res)=>{

        

        if (res.data.message === "login successful" && res.data.token) {
          alert("success");
          localStorage.setItem('token', res.data.token);
          navigate('/home');
        } else {
          alert("login failed");  // You can add an alert or handle the failure differently
        }
        })
      }
        else {
          alert("invalid credential")
        }
    } catch (err) {
      console.log(err)
      setErrors({ general: "An error occurred. Please try again later." });
    }
  };
  return (
    <>
    <div className="container">
    <div className='container col-3 login-box '>
      
      <form >
      <h1>Login</h1>
        <div className='input'>
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {errors.email && <p>{errors.email}</p>}

          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {errors.password && <p>{errors.password}</p>}

          {errors.general && <p>{errors.general}</p>}
        </div>
        
       <button className='but1' type="submit" onClick={handleLogin}>Login </button>
      
    
     
     
      </form>
    </div>
    </div>
    </>
  );
  };


export default LoginBox;
