import React, { useEffect, useState } from 'react';
import './SignUpBox.css';
import axios from 'axios';
import {BASE_URL}from '../../Constants/constants';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBCheckbox,
  MDBIcon
}
from 'mdb-react-ui-kit';


function SignUpBox({setBoxName}){

  const handleLogin=()=>{
    setBoxName('login')
}
const [signUpData,setSignUpData]=useState(
  { 
    fName:'',
    lName:'',
    email:'',
    password:'',
    confirmPassword:'',
    })
    
    useEffect(()=>{
      console.log(signUpData);
    },[signUpData])
    
      
    
  
 /*const [fname, setFname] = useState('');
  const [lname, setLname] = useState('');

 const [email, setEmail] = useState('');
 const [password, setPassword] = useState('');
 const [confirmPassword, setConfirmPassword] = useState('');

 const [error, setError] = useState('');*/
 const handleRegister = async (e) => {
  try {
    const res= await axios.post(`${BASE_URL}/auth/signUp`, signUpData).then((res)=>{
    debugger;
    console.log(res)});
     
  } catch (error) {
    
  }
};


const validateForm = () => {
  let newErrors = {};

  // Add your validation logic here
  if (formData.fName.trim() === '') {
    newErrors.fName = 'First name is required';
  }

  // Add other validation rules for lName, email, password, and confirmPassword

  setErrors(newErrors);
  return Object.keys(newErrors).length === 0;
};

 return (
  
 
  <MDBCol md='6' >

 <MDBCard className='my-5 bg-glass'>
 <MDBCardBody className='p-5'>
 <MDBRow>
  <MDBCol col='4'>
    <MDBInput
      wrapperClass='mb-4'
      label='First name'
      id='form1'
      type='fName'
      value={signUpData.fName}
      onChange={(e) => { setSignUpData({ ...signUpData, fName: e.target.value }); }}
    />
  </MDBCol>

  <MDBCol col='4'>
    <MDBInput
      wrapperClass='mb-4'
      label='Last name'
      id='form2'
      type='lName'
      value={signUpData.lName}
      onChange={(e) => { setSignUpData({ ...signUpData, lName: e.target.value }); }}
    />
  </MDBCol>
</MDBRow>

<MDBInput
  wrapperClass='mb-4'
  label='Email'
  id='form3'
  type='email'
  value={signUpData.email}
  onChange={(e) => { setSignUpData({ ...signUpData, email: e.target.value }); }}
/>
<MDBInput
  wrapperClass='mb-4'
  label='Password'
  id='form4'
  type='password'
  onChange={(e) => { setSignUpData({ ...signUpData, password: e.target.value }); }}
/>
<MDBInput
  wrapperClass='mb-4'
  label='Confirm Password'
  id='form5'
  type='password'
  onChange={(e) => { setSignUpData({ ...signUpData, confirmPassword: e.target.value }); }}
/>

   

   <MDBBtn className='w-100 mb-4' size='md'onClick={handleRegister}>sign up</MDBBtn>

   <div className="text-center">
  
<p><span onClick={handleLogin}>go to login</span> </p>
    
     <p>or sign up with:</p>
    
   </div> 

 </MDBCardBody>
</MDBCard>

</MDBCol>
 )
}
export default SignUpBox;
