import React, { useState } from 'react';

import {MDBContainer, MDBCol, MDBRow, MDBBtn, MDBIcon, MDBInput, MDBCheckbox } from 'mdb-react-ui-kit';

import './Login.css';

const Login= () => {
 const [email, setEmail] = useState('');
 const [password, setPassword] = useState('');

 const handleEmailChange = (e) => {
    setEmail(e.target.value);
 };

 const handlePasswordChange = (e) => {
    setPassword(e.target.value);
 };

 const handleSubmit = (e) => {
    e.preventDefault();

    // In a real app, you would send the email and password to your server for authentication
    console.log('Email:', email);
    console.log('Password:', password);

    // Reset the form fields
    setEmail('');
    setPassword('');
 };

 return (
  
        <MDBContainer fluid className='p-4 background-radial-gradient vh=100 '>
<MDBCol>
  <div className='loginpage'>
    <form onSubmit={handleSubmit}className='form'>
      <label htmlFor="email" className='em'>Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={handleEmailChange}
        required
      />

      <label htmlFor="password"className='pass'>Password:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={handlePasswordChange}
        required
      />
<div className='button'>
      <button type="submit"className="but1">Login</button>
      <button type="submit"className='but1'>signup</button></div>
      <a href="#!" className="text-body">Forgot password?</a>

    </form> </div></MDBCol>  
    </MDBContainer>

 );
 
};

export default Login;
