import React, { useState } from 'react';
import './AddCourtForm.css';
import MainNavBar from './MainNavBar';
import Footer from './Footer';

const AddCourtForm = () => {
  const [formData, setFormData] = useState({
    courtName: '',
    sportType: '',
    location: '',
    description: '',
    address: '',
    courtImage: null,
  });

  const [selectedImage, setSelectedImage] = useState('');

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, type, files, value } = e.target;
    const inputValue = type === 'file' ? files[0] : value;

    setFormData({
      ...formData,
      [name]: inputValue,
    });

    if (type === 'file' && inputValue) {
      try {
        const imageUrl = URL.createObjectURL(inputValue);
        setSelectedImage(imageUrl);
      } catch (error) {
        console.error('Error creating object URL:', error);
      }
    }
  };

  const validateForm = () => {
    let isValid = true;
    const newErrors = {};

    if (!formData.courtName.trim()) {
      newErrors.courtName = 'Court name is required';
      isValid = false;
    }

    if (!formData.sportType.trim()) {
      newErrors.sportType = 'Sport type is required';
      isValid = false;
    }

    if (!formData.location.trim()) {
      newErrors.location = 'Location is required';
      isValid = false;
    }

    if (!formData.description.trim()) {
      newErrors.description = 'Description is required';
      isValid = false;
    }

    if (!formData.address.trim()) {
      newErrors.address = 'Address is required';
      isValid = false;
    }

    if (!formData.courtImage) {
      newErrors.courtImage = 'Court image is required';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Form submitted:', formData);
      // Add logic for submitting the form, such as making an API call or dispatching an action
    }
  };

  return (
    <div>
      <MainNavBar />
      <div className="add-court-form">
        <h2>Add a Court</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="courtName">Court Name</label>
            <input
              type="text"
              id="courtName"
              name="courtName"
              value={formData.courtName}
              onChange={handleChange}
            />
            {errors.courtName && <p className="error-message">{errors.courtName}</p>}
          </div>

          {/* Add other form fields similarly */}

          <div className="form-group">
            <label htmlFor="courtImage">Court Image</label>
            <input
              type="file"
              id="courtImage"
              name="courtImage"
              accept="image/*"
              onChange={handleChange}
            />
            {errors.courtImage && <p className="error-message">{errors.courtImage}</p>}
          </div>

          {selectedImage && (
            <div className="image-preview Container">
              <img src={selectedImage} alt="Court Preview" width={'800px'} />
            </div>
          )}

          <button type="submit">Add Court</button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default AddCourtForm;
