import React, { useState, useEffect } from 'react';
import MainNavBar from '../Components/Common/MainNavBar';
import AxiosInstance from '../config/AxiosInstance';
import { useParams } from 'react-router-dom';
import courtUserViewPage from '../Pages/courtUserViewPage';
const CourtBooking = () => {
  const { id } = useParams();
  const [courtData, setCourtData] = useState({});
  const [timeslot, setTimeslot] = useState('');

  useEffect(() => {
    const fetchCourtDetails = async () => {      try {
        const response = await AxiosInstance.get(`/api/courts/${courtDataid}`);
        setCourtData(response.data);
      } catch (error) {
        console.error('Error fetching court details:', error);
      }
    };

    if (id) {
      fetchCourtDetails();
    }
  }, [id]);

  const handleAddTimeslot = async () => {
    try {
      // Perform the API request to add the timeslot
      const response = await AxiosInstance.post(`/api/courts/${id}/addTimeslot`, {
        timeslot: timeslot,
      });

      // Handle success (you can show a success message or perform additional actions)
      console.log('Timeslot added successfully:', response.data);
    } catch (error) {
      // Handle error (show an error message or perform error-related actions)
      console.error('Error adding timeslot:', error);
    }
  };

  return (
    <>
      <MainNavBar />
      <div className="container">
        <div className="row mt-4">
          <div className="col-md-6">
            <h2>{courtData.courtName}</h2>
            <img
              src={`${process.env.REACT_APP_BASE_URL}/courts/${courtData.courtImage}`}
              alt={courtData.courtName}
              className="img-fluid"
            />
          </div>
          <div className="col-md-6">
            <h3>Add Timeslot</h3>
            <div className="mb-3">
              <label htmlFor="timeslot" className="form-label">
                Timeslot:
              </label>
              <input
                type="text"
                id="timeslot"
                className="form-control"
                value={timeslot}
                onChange={(e) => setTimeslot(e.target.value)}
              />
            </div>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleAddTimeslot}
            >
              Add Timeslot
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default CourtBooking;
