// ... (import statements)

function SignUpBox({ setBoxName }) {
  const handleLogin = () => {
    setBoxName('login');
  };

  const [signUpData, setSignUpData] = useState({
    fName: '',
    lName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    let newErrors = {};

    if (signUpData.fName.trim() === '') {
      newErrors.fName = 'First name is required';
    }

    if (signUpData.lName.trim() === '') {
      newErrors.lName = 'Last name is required';
    }

    if (signUpData.email.trim() === '') {
      newErrors.email = 'Email is required';
    } else if (!/^\S+@\S+\.\S+$/.test(signUpData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (signUpData.password.trim() === '') {
      newErrors.password = 'Password is required';
    } else if (signUpData.password.length < 8) {
      newErrors.password = 'Password must be at least 8 characters';
    }

    if (signUpData.confirmPassword.trim() === '') {
      newErrors.confirmPassword = 'Confirm Password is required';
    } else if (signUpData.confirmPassword !== signUpData.password) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors({ ...newErrors });
    return Object.keys(newErrors).length === 0;
  };

  const handleRegister = async () => {
    if (!validateForm()) {
      return;
    }

    try {
      const res = await axios.post(`${BASE_URL}/auth/signUp`, signUpData);
      console.log(res);
      // Handle success (e.g., redirect or show a success message)
    } catch (error) {
      console.error('Error during signup API call:', error);
      // Handle the error (show a message to the user or log it)
    }
  };

  return (
    <MDBCol md='6'>
      <MDBCard className='my-5 bg-glass mt-0 '>
        <MDBCardBody className='p-5 '>
          {/* ... (other parts of your component) */}
          
          {/* Display error messages */}
          {errors.fName && <p className="error">{errors.fName}</p>}
          {errors.lName && <p className="error">{errors.lName}</p>}
          {errors.email && <p className="error">{errors.email}</p>}
          {errors.password && <p className="error">{errors.password}</p>}
          {errors.confirmPassword && <p className="error">{errors.confirmPassword}</p>}
          
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
  );
}

export default SignUpBox;
