import React, { useState } from 'react';

import {MDBContainer, MDBCol, MDBRow, MDBBtn, MDBIcon, MDBInput, MDBCheckbox } from 'mdb-react-ui-kit';

import './Login.css';

const Login= () => {
 const [email, setEmail] = useState('');
 const [password, setPassword] = useState('');

 const handleEmailChange = (e) => {
    setEmail(e.target.value);
 };

 const handlePasswordChange = (e) => {
    setPassword(e.target.value);
 };

 const handleSubmit = (e) => {
    e.preventDefault();

    // In a real app, you would send the email and password to your server for authentication
    console.log('Email:', email);
    console.log('Password:', password);

    // Reset the form fields
    setEmail('');
    setPassword('');
 };

 return (
  
  <div className='background-radial-gradient 'style={{minHeight:'100vh'}}>
    <MDBContainer fluid className='p-4 background-radial-gradient min-vh-1 d-flex'>
      


<MDBCol md='6' className='text-center'>

  <h1 className="my-5 display-3 fw-bold ls-tight px-3" style={{color: 'hsl(218, 81%, 95%)'}}>
    The best offer <br />
    <span style={{color: 'hsl(218, 81%, 75%)'}}>for your business</span>
  </h1>

  <p className='px-3' style={{color: 'hsl(218, 81%, 85%)'}}>
    Lorem ipsum dolor sit amet consectetur adipisicing elit.
    Eveniet, itaque accusantium odio, soluta, corrupti aliquam
    quibusdam tempora at cupiditate quis eum maiores libero
    veritatis? Dicta facilis sint aliquid ipsum atque?
  </p>

</MDBCol>




<MDBCol md='6' className='position-relative'>
  
    <form onSubmit={handleSubmit}className='form'>
      <label htmlFor="email" className='em'>Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={handleEmailChange}
        required
      />

      <label htmlFor="password"className='pass'>Password:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={handlePasswordChange}
        required
      />
<div className='button'>
      <button type="submit"className="but1">Login</button>
      <button type="submit"className='but1'>signup</button></div>
      <a href="#!" className="text-body">Forgot password?</a>

    </form> </MDBCol></MDBContainer></div>
    

 );
 
};

export default Login;
