import React, { useState } from 'react';
import './Login.css';

const Login= () => {
 const [email, setEmail] = useState('');
 const [password, setPassword] = useState('');

 const handleEmailChange = (e) => {
    setEmail(e.target.value);
 };

 const handlePasswordChange = (e) => {
    setPassword(e.target.value);
 };

 const handleSubmit = (e) => {
    e.preventDefault();

    // In a real app, you would send the email and password to your server for authentication
    console.log('Email:', email);
    console.log('Password:', password);

    // Reset the form fields
    setEmail('');
    setPassword('');
 };

 return (
  <div className='ba'>
    <form onSubmit={handleSubmit}className='FORM'>
      <label htmlFor="email" className='em'>Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={handleEmailChange}
        required
      />

      <label htmlFor="password"className='pass'>Password:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={handlePasswordChange}
        required
      />

      <button type="submit"className="but">Login</button>
    </form> </div>
 );

};


export default Login;
