import React, { useState, useEffect ,moda} from 'react';
import MainNavBar from '../Components/Common/MainNavBar';
import AxiosInstance from '../config/AxiosInstance';
import { useParams } from 'react-router-dom';
import { BASE_URL } from '../Constants/constants';
import TimeSlotModal from './Common/Modal';
Modal.setAppElement('#root');


const CourtBooking = () => {
  const [singleCourtData, setSingleCourtData] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [timeslot, setTimeslot] = useState('');

  const { id } = useParams();

  useEffect(() => {
    getSingleCourtData();
  }, []);

  const getSingleCourtData = () => {
    AxiosInstance.get('users/getSinglecourtData', { params: { courtId: id } })
      .then((res) => {
        setSingleCourtData(res.data);
      })
      .catch((err) => {
        console.log('Error in getting the data of a single court', err);
      });
  };

  const openModal = ({children}) => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleAddTimeSlot = () => {
    // Validate the timeslot (add more validation if needed)
    if (!timeslot.trim()) {
      alert('Please enter a valid time slot');
      return;
    }

    // Assume you have a state for storing time slots
    // You can replace this with your actual state management (Redux, Context, etc.)
    const updatedTimeSlots = [...timeslot, timeslot];

    // Update the state with the new time slots
    setTimeslot(updatedTimeSlots);

    // Close the modal
    closeModal();
  };

  return (
    <>
      <div className='single-court-img-box'>
        <img src={`${BASE_URL}/courts/${singleCourtData?.courtImage}`} alt="" />

        <div className='court-Name'>
          <h3>{singleCourtData?.courtName}</h3>
          <input type="text" placeholder="Enter time slot" />
          <button onClick={openModal}>Add Time Slot</button>
        </div>
      </div>

      {/* TimeSlotModal */}
      <TimeSlotModal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Add Time Slot Modal"
      >
        <h2>Add Time Slot</h2>
        <label htmlFor="timeslot">Time Slot:</label>
        <input
          type="text"
          id="timeslot"
          value={timeslot}
          onChange={(e) => setTimeslot(e.target.value)}
        />
        <button onClick={handleAddTimeSlot}>Add</button>
        <button onClick={closeModal}>Cancel</button>
      </TimeSlotModal>
    </>
  );
};

export default CourtBooking;
