  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBCheckbox,
  MDBIcon
}
from 'mdb-react-ui-kit';

const SignUpBox = ({setBoxName})=> {
  const handleLogin=()=>{
    setBoxName('login')
}
const [signUpData,setsignUpDAta]=useState(
  {
    fName:'',
    lName:'',
