 const handleRegister = async () => {
  try {
    const response = await axios.post(`${BASE_URL}/auth/signUp`, signUpData);
    
    if (response.data.message === "signup success") {
      setBoxName('login');
    }

    if (response.data.message === "email already exist") {
      alert('Email already exists');
    }
  } catch (error) {
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      console.error("Server responded with non-success status", error.response.status);
    } else if (error.request) {
      // The request was made but no response was received
      console.error("No response received from server");
    } else {
      // Something happened in setting up the request that triggered an Error
      console.error("Error during request setup", error.message);
    }
    alert('Something went wrong');
  }
};


 const validate = () => {
    if (!fname || !email || !password ) {
      setError('Please fill out all fields');
      return false;
    }

    if (password.length < 6) {
      setError('Password should be at least 6 characters long');
      return false;
    }

    setError('');
    return true;
 };

 const handleSubmit = (e) => {
    e.preventDefault();

    if (validate()) {
      // submit form
      console.log('Form submitted:', { name, email, password });
    }
 };

 return (
    <div className='container col-5'>
    <form className="signup-form" onSubmit={handleSubmit}>
      <h2>Sign Up</h2>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      {error && <p className="error">{error}</p>}
      <button type="submit">Sign Up</button>
      <div className='click'>
<p><span onClick={handleLogin}>go to login</span> </p>
      </div>
    </form></div>
    
