import React, { useState } from 'react';
import MainNavBar from '../Components/Common/MainNavBar';
import AxiosInstance from '../config/AxiosInstance';
import { useParams } from 'react-router-dom';
import TimeSlotModal from '../Components/TimeSlotModal';
import courtImage from '../asset/logo.jpg';

const CourtBooking = () => {
  const { id } = useParams();
  const [showModal, setShowModal] = useState(false);

  const handleAddTimeslot = async (timeslot) => {
    try {
      // Perform the API request to add the timeslot
      const response = await AxiosInstance.post(`/api/addTimeslot/${id}`, {
        timeslot: timeslot,
      });

      // Handle success (you can show a success message or perform additional actions)
      console.log('Timeslot added successfully:', response.data);
    } catch (error) {
      // Handle error (show an error message or perform error-related actions)
      console.error('Error adding timeslot:', error);
    }
  };

  return (
    <>
      <MainNavBar />

      <div className='single-court-img-box'>
        <img src={courtImage} alt="Court Image" />

        <div className='court-Name'>
          <h3>Court name</h3>
          <button onClick={() => setShowModal(true)}>Add</button>
        </div>
      </div>

      <TimeSlotModal
        show={showModal}
        onHide={() => setShowModal(false)}
        onAddTimeslot={handleAddTimeslot}
      />
    </>
  );
};

export default CourtBooking;
